// ProfileScreen.js
import React, { useEffect, useState } from 'react';
import { View, Text, Image } from 'react-native';
import { fetchData } from '../../data/TheMealDB';

const Profile = () => {
    const [recipe, SetRecipe] = useState([]);
    useEffect(async () => {
        try {
            const data = await fetchData("search.php?s=");
            console.log(data.meals[0]);
            SetRecipe(data.meals[0]);
        } catch (error) {
            console.log(error);
        }
    }, []);
    return (
        <View>
            <Image style={{ width: 200, height: 200 }} source={{ uri: recipe.strMealThumb }} />
            <Text>{recipe.strMeal}</Text>
            <Text>Category: {recipe.strCategory}</Text>
            <Text>Area: {recipe.strArea}</Text>
            <Text>Instructions: {recipe.strInstructions}</Text>
            {/* Render other details like ingredients and measurements */}
        </View>
    );
};

export default Profile;
