// ProfileScreen.js
import React, { useEffect, useState } from 'react';
import { View, Text, Image } from 'react-native';
import { fetchData } from '../../data/TheMealDB';

const Profile = () => {
    const [recipe, SetRecipe] = useState([]);
    useEffect(async () => {
        try {
            const data = await fetchData("search.php?s=");
            console.log(data.meals[0]);
            SetRecipe(data.meals[0]);
        } catch (error) {
            console.log(error);
        }
    }, []);
    const renderRecipeItem = ({ item }) => (
        <View>
            <Image style={{ width: 200, height: 200 }} source={{ uri: item.strMealThumb }} />
            <Text>{item.strMeal}</Text>
            <Text>Category: {item.strCategory}</Text>
            <Text>Area: {item.strArea}</Text>
            <Text>Instructions: {item.strInstructions}</Text>
            {/* Render other details like ingredients and measurements */}
        </View>
    );

    return (
        <FlatList
            data={recipes}
            renderItem={renderRecipeItem}
            keyExtractor={(item) => item.idMeal}
        />
    );
};

export default Profile;
